import './App.css';
import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import {ContactsPage} from './containers/contactsPage';
import {AppointmentsPage} from './containers/appointmentsPage';

export default function App() {
  // This is a STATEFUL component
  const [contacts, setContacts] = useState([]);
  const [appointments, setAppointments] = useState([]);

  // There has to be a function to be passed as a prop in order to give the ability to other components to modify the states
  const addContact = (name,phone,number,email) => {
    setContacts(prev => [...prev, {
      name: name,
      phone: phone,
      number: number,
      email: email
    }])
  }
  const addAppointment = (title,contact,date,time) => {
    setAppointments(prev => [...prev, {
      title: title,
      contact: contact,
      date: date,
      time: time
    }])
  }

  return (
    <div>
      {//<Link to='/contacts'>Contacts</Link>
      }
      <ContactsPage contacts={contacts} addContact={addContact} />
      {//<Link to='/appointments'>Appointments</Link>
      }
      <AppointmentsPage appointments={appointments} contacts={contacts} addAppointment={addAppointment} />
    </div>
  )
}
