import React, { useEffect, useState } from 'react';
import { ContactForm } from '../components/contactForm';
import { TileList } from '../components/tileList';


export function ContactsPage({contacts,addContact}) {
    // This is the parent component that will CONTROL the Form component
    // This is a STATEFUL component
    // Component for handling logic for adding contacts and listing current contacts
    const [nameInput, setNameInput] = useState('');
    const [phoneInput, setPhoneInput] = useState('');
    const [emailInput, setEmailInput] = useState('');
    const [isDuplicate, setIsDuplicate] = useState(false);

    useEffect(() => {
        for(let i=0; i < contacts.length; i++) {
            console.log(contacts[i].name)
            if(contacts[i].name === nameInput) {
                setIsDuplicate(true);
            } else if(contacts[i].name !== nameInput) {
                setIsDuplicate(false);
            }
        }
    }, [contacts, nameInput])

    const handleSubmit = (e) => {
        e.preventDefault();
        if(!isDuplicate) {
            addContact(nameInput,phoneInput,emailInput);
            setNameInput('');
            setPhoneInput('');
            setEmailInput('');
        }
    }

    return (
        <div>
            <section>
                <h2>Add Contact</h2>
                <ContactForm 
                nameInput={nameInput}
                setNameInput={setNameInput}
                phoneInput={phoneInput}
                setPhoneInput={setPhoneInput}
                emailInput={emailInput}
                setEmailInput={setEmailInput}
                handleSubmit={handleSubmit}
                isDuplicate={isDuplicate}
                />
            </section>

            <section>
                <h2>Contacts</h2>
                <TileList
                array={contacts}
                />
            </section>
            
        </div>
    );
}
